import React, {useEffect, useState} from 'react';
import Personajes from './assets/componentes/Personajes';
import Navegacion from './assets/componentes/Navegacion'
import {BrowserRouter, Routes, Route, Link} from 'react-router-dom'
import Hed from './assets/componentes/Hed'

function App() {

  const [characters, setCharacters] = useState ([]);

  const iniUrl = 'https://rickandmortyapi.com/api/character'

  const infor = (url) =>{ fetch(url)
  .then (response => response.json())
  .then (data => setCharacters (data.results))
  .then (error => console.log(error))
  }

  useEffect (() =>{
      infor (iniUrl);
  }, [])


  return (
    <>
     <BrowserRouter>   

     <Navegacion/>
      <Routes>
          <Route path='/' exact>
              <Hed/>
          </Route>
          <Route path='/personajes'>
            <Personajes personaje ={characters}/>
          </Route>
      </Routes>
      </BrowserRouter>




   </>
  );
}

export default App;
