import React, {useEffect, useState} from 'react';
// import Navegacion from './assets/componentes/Navegacion';
// import { Container } from '@mui/material';
import Personajes from './assets/componentes/Personajes';

function App() {

  const [characters, setCharacters] = useState ([]);

  const iniUrl = 'https://rickandmortyapi.com/api/episode'

  const infor = (url) =>{ fetch(url)
  .then (response => response.json())
  .then (data => setCharacters (data.results))
  .then (error => console.log(error))
  }

  useEffect (() =>{
      infor (iniUrl);
  }, [])


  return (
    <>
    <Personajes personaje = {characters}/>

   </>
  );
}

export default App;
