import axios from "axios"

//componentes

const dataIni = {
    array : []
}

const obtenerRm = 'obtenerRm'
//reducer

export default function rmReducer (state = dataIni, action) {
    switch(action.type){
        case obtenerRm :
            return {...state, array: action.payload}
        default:
            return state

    }

}

//acciones
export const obtermAccion = () => async (dispatch, getState) => {
    try {
        const res = await axios.get ('https://rickandmortyapi.com/api/character?page=1')
        dispatch ({
            type: obtenerRm, 
            payload: res.data.results
        })
    }
    catch (error){
        console.log(error);
    }

}
