import React from 'react'
import {useDispatch, useSelector} from 'react-redux'
import {obtermAccion, siguienteList,antesList} from 'C:/Users/d_b_o/Desktop/dev_dan/r_m/src/redux/r_mDucks'
import { Button, Container } from '@mui/material'
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { blueGrey } from '@mui/material/colors';

const theme = createTheme({
  palette: {
    primary: {
      // Purple and green play nicely together.
      main: blueGrey[500],
    },
    secondary: {
      // This is green.A700 as hex.
      main: '#11cb5f',
    },
  },
});


const ListaPer = () =>{
    const dispatch = useDispatch ()
    const personaje = useSelector (store => store.personajes.array)
    console.log(personaje);


  return (
    <div>
        <Container maxWidth="xs">
            <ThemeProvider theme={theme}>
        {window.onload= () => dispatch (obtermAccion()) }
       <Button onClick={() => dispatch (siguienteList())}>Siguiente</Button>
        <Button onClick={() => dispatch (antesList())}>Anterior</Button>
        </ThemeProvider>
        </Container>
        <div className='row' >
                  { personaje.map(per => {
                      return( 
                        <div  className='col'>
                          <div  className='card'>
                            <img src={per.image} alt="" />
                              <h5 className=' mt-2' key={per.id}>
                                  Nombre: {per.name}
                              </h5>
                              <h5>
                                  Especie: {per.species}
                              </h5>
                              <h5>
                                Estado : {per.status}
                              </h5>
                          </div>

                        </div>
                  )})

                  }
                  
      
        </div>
        <Container maxWidth="xs">
            {window.onload= () => dispatch (obtermAccion()) }
            <Button onClick={() => dispatch (siguienteList())}>Siguiente</Button>
           <Button onClick={() => dispatch (antesList())}>Anterior</Button>
        </Container>

    </div>
  )
}

export default ListaPer;
