import React from 'react'


const Personajes = ({personaje = []}) =>{
return(

<div className='row' >
                  { personaje.map(per => {
                      return( <div  className='col'>
                          <div  className='card'>
                              <img src={per.image} alt="" />
                              <h5 className=' mt-2'>
                                  Nombre: {per.name}
                              </h5>
                              <h5>
                                  Especie: {per.species}
                              </h5>

                              
                              <br/>
                          </div>

                      </div>
                  )})

                  }
                  
      
    </div>
)
}

export default Personajes
